import React, { useState } from "react";
import styles from "./User.module.css";


const users = [
  {
    firstName: "Test1",
    lastName: "Testovich1",
    age: 28,
    photoSrc: "https://sigc.edu/sigc/ad-sigc/datas/images/userimg.jpg",
  },
  {
    firstName: "Test2",
    lastName: "Testovich2",
    age: 31,
    photoSrc:
      "https://images.pexels.com/photos/1858175/pexels-photo-1858175.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
  },
  {
    firstName: "Test3",
    lastName: "Testovich3",
    age: 70,
    photoSrc:
      "https://images.pexels.com/photos/573299/pexels-photo-573299.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
  },
  {
    firstName: "Test4",
    lastName: "Testovich4",
    age: 20,
    photoSrc:
      "https://images.pexels.com/photos/2379004/pexels-photo-2379004.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
  },
  {
    firstName: "Test5",
    lastName: "Testovich5",
    age: 28,
    photoSrc:
      "https://images.pexels.com/photos/415829/pexels-photo-415829.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
  },
];

function User(props) {
  const { isLight } = props;
  const [currentIndex, setCurrentIndex] = useState(0);  

  function next() {
    setCurrentIndex((currentIndex + 1) % users.length); // the same as if currentIndex === users.length go to the next circle
  }

  function prev() {
    setCurrentIndex((currentIndex - 1 + users.length) % users.length); // the same - go to the next circle but return back
  }

  const buttonStyle = {
    border: `1px solid ${isLight ? 'black' : 'white'}`,
    backgroundColor: isLight ? 'white' : 'black',
    color: isLight ? 'black' : 'white'
  }

  const { photoSrc, firstName, lastName, age } = users[currentIndex];
 
  return (
    <>
      <article className={styles.userCard}>
        <img src={photoSrc} alt={firstName} />
        <p>
          {firstName} {lastName}
        </p>
        <p> {age}</p>
      </article>
      <div className={styles.buttonContainer}>
        <button style={buttonStyle} onClick={prev}>{"<"}</button>
        <button style={buttonStyle} onClick={next}>{">"}</button>
      </div>
    </>
  );
}

export default User;
